import React, { useState } from "react";
import SplashScreen from "./splashScreen";

export const Login = () => {
    const [email,setEmail] = useState("");
    const [loading,setLoading] = useState(false);

  return <>
  <div>
        <div className="item">
          <h2>{t("Login.2")}</h2>
          <input
            onChange={(e) => setEmail(e.target.value)}
            value={email}
            type="text"
            id="email"
            name="email"
            className="text-input"
            placeholder="email"
          />

          <div className="passShow"></div>
          <input
            onChange={(e) => setPassword(e.target.value)}
            type="password"
            id="password"
            name="password"
            value={password}
            onKeyUp={validatePassword}
            className="password-input"
            placeholder={t("Login.4")}
          />

          <input
            onChange={(e) => handlePassword2(e.target.value)}
            placeholder={t("Login.5")}
            onKeyUp={validatePassword}
            value={password2}
            type="password"
            id="password2"
            name="password"
            className="password-input"
          />

          {password2 && (
            <div className="passRule">
              <h1 style={passwordMatch ? { color: "#4adeb5" } : {}}>
                Password Match
              </h1>
            </div>
          )}
          {!password2 && (
            <div className="passInclude">
              <div className="passRule">
                <h1 style={contains7C ? { color: "#4adeb5" } : {}}>
                  {t("Login.29")}{" "}
                </h1>
              </div>

              <div className="passRule">
                {" "}
                <h1 style={containsUL ? { color: "#4adeb5" } : {}}>
                  {t("Login.31")}
                </h1>
              </div>

              <div className="passRule">
                {" "}
                <h1 style={containsN ? { color: "#4adeb5" } : {}}>
                  {t("Login.32")}
                </h1>
              </div>
            </div>
          )}
          {!success && <h1>User already exists and password doesnt match</h1>}

          <button
              type="button"
              className="forwardButton"
              disabled={false}
              onClick={()=>setLoading(true)}

            >

          {loading && <b>Loading</b>}
          {!loading && <b>{t("Login.6")}</b>}



            </button>
        </div>
      </div>
  </>;
};

export default Login;
